package cn.jbolt.common.model;

import com.jfinal.kit.StrKit;

import cn.jbolt.common.model.base.BaseSysNotice;
import cn.jbolt.core.annotation.TableBind;
import cn.jbolt.core.base.JBoltIDGenMode;
import cn.jbolt.core.cache.JBoltDictionaryCache;
import cn.jbolt.core.cache.JBoltUserCache;

/**
 * 系统通知
 * Generated by JBolt.
 */
@SuppressWarnings("serial")
@TableBind(dataSource = "main" , table = "jb_sys_notice" , primaryKey = "id" , idGenMode = JBoltIDGenMode.SNOWFLAKE)
public class SysNotice extends BaseSysNotice<SysNotice> {
	public String getTypeName() {
		return JBoltDictionaryCache.me.getNameBySn("sys_notice_type", getType()+"");
	}
	
	public String getCreateUserName() {
		return JBoltUserCache.me.getName(getCreateUserId());
	}
	
	public String getUpdateUserName() {
		return JBoltUserCache.me.getName(getUpdateUserId());
	}
	public String getPriorityLevelName() {
		return JBoltDictionaryCache.me.getNameBySn("sys_notice_priority_level", getPriorityLevel()+"");
	}
	
	public String getReceiverTypeName() {
		return JBoltDictionaryCache.me.getNameBySn("sys_notice_receiver_type", getReceiverType()+"");
	}
	
	public String getReceiverValues() {
		return get("receiverValues","未设置");
	}
	
	public Boolean getHasFile() {
		return StrKit.notBlank(getFiles());
	}
	public Boolean getReaded() {
		return StrKit.notBlank(getStr("readed"));
	}
}
